package com.animeforum.app.utils
object ValidationHelper{ data class ValidationResult(val isValid:Boolean, val errorMessage:String=""); fun validateUsername(s:String)=when{ s.isBlank()->ValidationResult(false,"El nombre de usuario es requerido"); s.length<3->ValidationResult(false,"Mínimo 3 caracteres"); s.length>20->ValidationResult(false,"Máximo 20 caracteres"); !s.matches(Regex("^[a-zA-Z0-9_]+$"))->ValidationResult(false,"Solo letras, números y guión bajo"); else->ValidationResult(true)}; fun validateEmail(e:String)=when{ e.isBlank()->ValidationResult(false,"El email es requerido"); !android.util.Patterns.EMAIL_ADDRESS.matcher(e).matches()->ValidationResult(false,"Email inválido"); else->ValidationResult(true)}; fun validateAnimeTitle(t:String)=when{ t.isBlank()->ValidationResult(false,"El título del anime es requerido"); t.length<2->ValidationResult(false,"Mínimo 2 caracteres"); t.length>100->ValidationResult(false,"Máximo 100 caracteres"); else->ValidationResult(true)}; fun validateMessage(m:String)=when{ m.isBlank()->ValidationResult(false,"El mensaje es requerido"); m.length<10->ValidationResult(false,"Mínimo 10 caracteres"); m.length>500->ValidationResult(false,"Máximo 500 caracteres"); else->ValidationResult(true)}; fun validateCategory(c:String):ValidationResult{ val valid=listOf("Todos","Shonen","Seinen","Shoujo","Isekai","Mecha","Slice of Life"); return if(c.isBlank()) ValidationResult(false,"Selecciona una categoría") else if(c !in valid) ValidationResult(false,"Categoría inválida") else ValidationResult(true) }; fun validateRating(r:Int)=when{ r<1->ValidationResult(false,"La calificación mínima es 1"); r>5->ValidationResult(false,"La calificación máxima es 5"); else->ValidationResult(true)} }